CREATE TABLE IF NOT EXISTS friends (id_friend INTEGER PRIMARY KEY AUTOINCREMENT, name_friend TEXT, address_friend TEXT, phone_friend INTEGER );
CREATE TABLE IF NOT EXISTS items (id_item INTEGER PRIMARY KEY AUTOINCREMENT, name_item TEXT, buy_date TEXT, price DOUBLE, cons_state TEXT, type INTEGER);
CREATE TABLE IF NOT EXISTS loan (id_loan INTEGER PRIMARY KEY AUTOINCREMENT, id_friend INTEGER REFERENCES friends(id_friend) ON UPDATE CASCADE ON DELETE RESTRICT , loan_date TEXT );
CREATE TABLE IF NOT EXISTS loan_has_item (has_loan INTEGER REFERENCES loan(id_loan) ON UPDATE CASCADE ON DELETE RESTRICT, has_item INTEGER REFERENCES items(id_item) ON UPDATE CASCADE ON DELETE RESTRICT);